  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
    searchTerm:String="";
    constructor(private route : ActivatedRoute){}

    ngOnInit():void{}

    onclick($event:any){
      console.log($event)
    }
}
<div>
