import { Component } from '@angular/core';
import { CartService } from '../services/cart/cart.service';
import { Cart } from '../shared/models/Cart';
import { CartItem } from '../shared/models/CartItem';
import { FoodService } from '../services/food/food.service';

@Component({
  selector: 'app-cart-page',
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.css']
})
export class CartPageComponent {
  cart!:Cart;
  constructor(private cartService : CartService , private foodSevice:FoodService){
    let foods = foodSevice.getAll();
    cartService.addToCart(foods[1]);
    cartService.addToCart(foods[3]);
    cartService.addToCart(foods[4]);
    this.setCart();
  }

  ngOnInit():void{

  }

  setCart(){
    this.cart = this.cartService.getCart();
  }

  removeFromCart(cartItem:CartItem){
    this.cartService.removeFromCart(cartItem.food.id);
    this.setCart();
  }

  changeQuantity(cartItem :CartItem,quantityInString:string){
    let quantity = parseInt(quantityInString);
    console.log(quantity);
    this.cartService.changeQuantity(cartItem.food.id, quantity);
    this.setCart();
    console.log(this.cart.totalPrice)
  }
}
<div class="container">
  <ul>
    <li *ngFor="let cartItem of cart.items">
      <div>
        <img [src]="cartItem.food.imageUrl" alt="food name" />
      </div>
      <div>
        <a routerLink="/food/{{ cartItem.food.id }}">{{
          cartItem.food.name
        }}</a>
      </div>
      <div>
        <select #quantityselect (change)="changeQuantity(cartItem, quantityselect.value)">
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </select>
      </div>
      <div>
        {{ cartItem.price | currency }}
      </div>
      <button class="remove-button" (click)="removeFromCart(cartItem)">
        Remove
      </button>
    </li>
  </ul>

  <div class="checkout">
    <div>
      <div class="foods-count">{{ cart.items.length }}</div>
      <div class="total-price">{{ cart.totalPrice | currency }}</div>
    </div>
  </div>
</div>
