import { Component } from '@angular/core';
import { Tag } from '../shared/models/Tag';

@Component({
  selector: 'app-tags',
  templateUrl: './tags.component.html',
  styleUrls: ['./tags.component.css']
})
export class TagsComponent {

  tags:Tag[] = [];
  constructor(){}

  ngOnInit():void{}
}
<div *ngIf="tags.some()">
    <a *ngFor="let tag of tags">
        {{tag.name}}({{tag.count}})
    </a>
</div>
