import { Component } from '@angular/core';
import { Food } from '../shared/models/Food';
import { ActivatedRoute, Router } from '@angular/router';
import { FoodService } from '../services/food/food.service';
import { CartService } from '../services/cart/cart.service';

@Component({
  selector: 'app-foodpage',
  templateUrl: './foodpage.component.html',
  styleUrls: ['./foodpage.component.css']
})
export class FoodpageComponent {

    food!:Food;
    constructor(private activatedRoute:ActivatedRoute,private foodService:FoodService,private cartService :CartService,private router:Router){
      activatedRoute.params.subscribe((params:any)=>{
        if(params.id){
          this.food = foodService.getFoodById(params.id);
        }
      })
      console.log(this.food);
    }


    ngOnInit():void{

    }

    addToCart(){
      this.cartService.addToCart(this.food);
      this.router.navigateByUrl('/cart-page')
    }
}
<div *ngIf="!food">
   if food is not there
</div>

<div *ngIf="!food; else elseBlock">
    empty
</div>
<ng-template #elseBlock>
    <div class="container">
        <img src="{{food.imageUrl}}" alt="{{food.name}}">

        <div class="details">
            <div class="header">
                <span class="name">
                    {{food.name}}
                </span>
                <span class="favorite {{food.favorite}}?'' : 'not' ">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-suit-heart-fill" viewBox="0 0 16 16">
                        <path d="M4 1c2.21 0 4 1.755 4 3.92C8 2.755 9.79 1 12 1s4 1.755 4 3.92c0 3.263-3.234 4.414-7.608 9.608a.513.513 0 0 1-.784 0C3.234 9.334 0 8.183 0 4.92 0 2.755 1.79 1 4 1z"/>
                    </svg>
                </span>
            </div>

            <div class="origins">
                <span *ngFor="let origin of food.origins">
                    {{origin}}
                </span>
            </div>

            <div class="tags">
                <app-tags [foodPageTags]="food.tags" justifyContent="start"></app-tags>
            </div>
            <div class="cook-time">
                Time to cook <strong>{{food.cookTime}}</strong> minutes
            </div>
            <div class="price">
                <span>
                    {{food.price|currency}}
                </span>
            </div>
            <button (click)="addToCart()">Add to cart</button>
        </div>
    </div>

</ng-template>


